import { ref, inject } from 'vue'

import { useMessage, useDialog } from 'naive-ui'

import axios from 'axios'

import QueryString from 'qs'

export default () => {

    // 接收全局变量
    const global = inject('global');

    const message = useMessage();

    // 房屋 模态框
    const housingModelStatus = ref(false); // false 添加 true 修改
    const housingFormRef = ref(null);
    const showHousingModal = ref(false) // 模态框是否打开

    // 提交按钮加载
    const housingSubmitLoading = ref(false);

    // 表单校验
    const housingRules = {
        id: {
            required: true,
            message: "请输入楼栋编号",
            trigger: "blur"
        },
        name: {
            required: true,
            message: "请输入楼栋名称",
            trigger: "blur"
        },
        area: {
            required: true,
            message: "请输入建筑面积",
            trigger: "blur"
        },
    }

    // 表单输入数据
    const housingValue = ref({
        id: "",
        name: "",
        area: "",
        remark: "",
    })

    const houseChildrenType = [{
        value: "一厅",
        label: "一厅",
    },{
        value: "二厅",
        label: "二厅",
    },{
        value: "三厅",
        label: "三厅",
    },{
        value: "四厅",
        label: "四厅",
    },{
        value: "五厅",
        label: "五厅",
    },{
        value: "六厅",
        label: "六厅",
    },{
        value: "七厅",
        label: "七厅",
    },{
        value: "八厅",
        label: "八厅",
    }]

    const houseType = [{
        value: "二室",
        label: "二室",
        children: houseChildrenType
    },{
        value: "三室",
        label: "三室",
        children: houseChildrenType
    },{
        value: "四室",
        label: "四室",
        children: houseChildrenType
    },{
        value: "五室",
        label: "五室",
        children: houseChildrenType
    },{
        value: "六室",
        label: "六室",
        children: houseChildrenType
    },{
        value: "七室",
        label: "七室",
        children: houseChildrenType
    },{
        value: "八室",
        label: "八室",
        children: houseChildrenType
    },{
        value: "一室",
        label: "一室",
        children: houseChildrenType
    }]

    return {
        housingModelStatus, // 房屋模态框状态
        housingFormRef, // 房屋 Form 引用
        showHousingModal, // 模态框是否打开
        housingSubmitLoading, // 提交按钮加载中
        housingRules, // 房屋 Form 校验信息
        housingValue, // 房屋 Form 参数
        houseType, // 户型
    }

}
