import { ref } from 'vue'

import { useMessage, useDialog } from 'naive-ui'

export default () => {

    const message = useMessage();
    const dialog = useDialog();

    // 单元 模态框
    const apartmentStatus = ref(false); // false 添加 true 修改
    const apartmentFormRef = ref(null);
    const showApartmentModal = ref(false) // 模态框是否打开
    // 表单校验
    const apartmentRules = {
        id: {
            required: true,
            message: "请输入楼栋编号",
            trigger: "blur"
        },
        name: {
            required: true,
            message: "请输入楼栋名称",
            trigger: "blur"
        },
        area: {
            required: true,
            message: "请输入建筑面积",
            trigger: "blur"
        },
    }

    // 表单输入数据
    const apartmentValue = ref({
        apartment: {
            id: "",
            name: "",
            area: "",
            remark: "",
        },
    })

    // 表单提交事件
    const apartmentSubmit = (e) => {
        e.preventDefault();
        apartmentFormRef.value?.validate((errors) => {
            if (!errors) {
                // 校验通过
            } else {
                // 校验不通过
            }
        });
    }

    return {
        buildingStatus,
        buildingFormRef,
        showBuildingModal,
        buildingRules,
        buildingValue,
        buildingSubmit
    }
}
