import { ref } from 'vue'

export default () => {

    // 添加楼栋 模态框
    const buildingStatus = ref(false); // false 添加 true 修改
    const buildingFormRef = ref(null);
    const showBuildingModal = ref(false) // 模态框是否打开
    // 表单校验
    const buildingRules = {
        id: {
            required: true,
            message: "请输入楼栋编号",
            trigger: "blur"
        },
        name: {
            required: true,
            message: "请输入楼栋名称",
            trigger: "blur"
        },
        area: {
            required: true,
            message: "请输入建筑面积",
            trigger: "blur"
        },
    }

    // 表单输入数据
    const buildingValue = ref({
        building: {
            id: "",
            name: "",
            area: "",
            remark: "",
        },
    })

    // 表单提交事件
    const buildingSubmit = (e) => {
        e.preventDefault();
        buildingFormRef.value?.validate((errors) => {
            if (!errors) {
                // 校验通过
            } else {
                // 校验不通过
            }
        });
    }

    // 删除楼栋
    const deleteBuilding = (id) => {
        dialog.warning({
            title: "请确认您的操作!",
            content: "确认是否删除!",
            positiveText: "确认删除",
            negativeText: "取消",
            onPositiveClick: () => {
                message.success("确认删除");
            }
        });
    }

    return {
        buildingStatus,
        buildingFormRef,
        showBuildingModal,
        buildingRules,
        buildingValue,
        buildingSubmit,
        deleteBuilding
    }
}
