// 添加业主
import { ref, inject } from 'vue'
import { useMessage } from 'naive-ui'
import QueryString from 'qs'
import axios from 'axios'

// 接收全局变量
const global = inject("global")
// 获取消息对象
const message = useMessage();

// 业主 模态框
const ownerStatus = ref(false); // false 添加 true 修改
const ownerFormRef = ref(null); // 业主 Form 引用
const showOwnerModal = ref(false) // 模态框是否打开

// 表单校验
const ownerRules = {
    user: {
        name: {
            required: true,
            message: "请输入姓名",
            trigger: "blur"
        },
        gender: {
            required: true,
            message: "请选择性别",
            trigger: "blur"
        },
        age: {
            required: true,
            message: "请输入年龄",
            trigger: "blur"
        },
        phone: {
            required: true,
            message: "请输入手机号码",
            trigger: "blur"
        },
    },
}
// 表单输入数据
const ownerformValue = ref({
    user: {
        name: "",
        gender: null,
        age: "",
        identity: "",
        phone: "",
        rke: "",
        remark: "",
        qq: ""
    },
})

// 表单提交事件
const addownerSubmit = (e) => {
    e.preventDefault();
    ownerFormRef.value?.validate((errors) => {
        if (!errors) {
            // 校验通过
        } else {
            // 校验不通过
        }
    });
}
// 性别选择框数据
const ownerGenderOptions = ref(['男', '女'].map(
    (v) => ({
        label: v,
        value: v
    })
))

/**
 * 打开模态框
 * @param {*} status false 添加 true 修改
 */
const openOwnerModal = (status) => {
    showOwnerModal.value = true;
    ownerStatus.value = status
}

/**
 * 关闭模态框
 */
const closeOwnerModal = () => {
    showOwnerModal.value = true;
}

// 导出
export {
    ownerStatus, // 模态 添加 or 修改 框状态
    ownerFormRef, // 业主 Form 引用
    showOwnerModal, // 显示模态框
    ownerRules, // 表单校验
    ownerformValue, // 表单参数
    ownerGenderOptions, // 性别选项数据
    openOwnerModal, // 打开模态框
    closeOwnerModal, // 关闭模态框
    addownerSubmit, // 表单提交
}
