import { ref,reactive } from 'vue'

import { useMessage, useDialog } from 'naive-ui'

import axios from 'axios'

export default () => {

    const message = useMessage();
    const dialog = useDialog();

    // 楼栋 模态框
    const buildingStatus = ref(false); // false 添加 true 修改
    const buildingFormRef = ref(null);
    const showBuildingModal = ref(false) // 模态框是否打开
    // 表单校验
    const buildingRules = {
        id: {
            required: true,
            message: "请输入楼栋编号",
            trigger: "blur"
        },
        name: {
            required: true,
            message: "请输入楼栋名称",
            trigger: "blur"
        },
        area: {
            required: true,
            message: "请输入建筑面积",
            trigger: "blur"
        },
    }

    // 表单输入数据
    const buildingValue = ref({
        id: "",
        name: "",
        area: "",
        remark: "",
    })

    // 表单提交事件
    const buildingSubmit = (e) => {
        e.preventDefault();
        buildingFormRef.value?.validate((errors) => {
            if (!errors) {
                // 校验通过
            } else {
                // 校验不通过
            }
        });
    }

    // 删除楼栋
    const deleteBuilding = (id) => {
        dialog.warning({
            title: "请确认您的操作!",
            content: "确认是否删除!",
            positiveText: "确认删除",
            negativeText: "取消",
            onPositiveClick: () => {
                message.success("确认删除 => " + id);
            }
        });
    }

    // 楼栋信息
    const buildingData = reactive([{
        "label": "1栋",
        "type": 0,
        "key": "403626",
        "children": [
          {
            "label": "1单元",
            "type": 1,
            "key": "40362610"
          },
          {
            "label": "2单元",
            "type": 1,
            "key": "40362611"
          },
          {
            "label": "6单元",
            "type": 1,
            "key": "40362612"
          },
          {
            "label": "12单元",
            "type": 1,
            "key": "40362613"
          }
        ]
      }, {
        "label": "2栋",
        "type": 0,
        "key": "403627",
        "children": [
          {
            "label": "1单元",
            "type": 1,
            "key": "40362614"
          },
          {
            "label": "2单元",
            "type": 1,
            "key": "40362615"
          },
          {
            "label": "6单元",
            "type": 1,
            "key": "40362616"
          },
          {
            "label": "12单元",
            "type": 1,
            "key": "40362617"
          }
        ]
      }, , {
        "label": "3栋",
        "type": 0,
        "key": "403628",
        "children": [
          {
            "label": "1单元",
            "type": 1,
            "key": "40362618"
          },
          {
            "label": "2单元",
            "type": 1,
            "key": "40362619"
          },
          {
            "label": "6单元",
            "type": 1,
            "key": "40362620"
          },
          {
            "label": "12单元",
            "type": 1,
            "key": "40362621"
          }
        ]
      }])

    // 加载楼栋信息
    axios({
        method: 'get',
        url: '/api/getAllBuildings',
    }).then(res => {
        for (let building of res.data) {
            buildingData.push({
                "id": building.buildingNo,
                "label": building.name,
                "type": 0,
                "key": building.id
            })
        }
        console.log(buildingData);
    }).catch(err => {
        message.error('数据加载异常！' + err, { duration: 5000 })
    })

    return {
        buildingStatus,
        buildingFormRef,
        showBuildingModal,
        buildingRules,
        buildingValue,
        buildingData,
        buildingSubmit,
        deleteBuilding
    }
}
