import { ref, inject, computed } from 'vue'

import { useMessage, useDialog } from 'naive-ui'

import axios from 'axios'

import QueryString from 'qs'

export default () => {

    // 接收全局变量
    const global = inject('global');

    const message = useMessage();

    // 房屋 模态框
    const housingModelStatus = ref(false); // false 添加 true 修改
    const housingFormRef = ref(null);
    const showHousingModal = ref(false) // 模态框是否打开

    // 提交按钮加载
    const housingSubmitLoading = ref(false);

    // 表单校验
    const housingRules = {
        id: {
            required: true,
            message: "请输入楼栋编号",
            trigger: "blur"
        },
        name: {
            required: true,
            message: "请输入楼栋名称",
            trigger: "blur"
        },
        area: {
            required: true,
            message: "请输入建筑面积",
            trigger: "blur"
        },
    }

    // 表单输入数据
    const housingValue = ref({
        housesNo: "", // 房屋编号
        floor: "", // 房屋楼层
        room: "", // 室
        hall: "", // 厅
        propertyType: computed({
            set(value) {
                housingValue.value.room = value.substring(0,2)
                housingValue.value.hall = value.substring(2)
            },
            get() {
                return housingValue.value.room + housingValue.value.hall
            }
        }), // 户型
        remark: "",
    })

    housingValue.value.propertyType = "一室一厅"
    console.log(housingValue.value.room);
    console.log(housingValue.value.hall);

    const houseType = [
        [{
            value: "一室",
            label: "一室",
        }, {
            value: "两室",
            label: "两室",
        }, {
            value: "三室",
            label: "三室",
        }, {
            value: "四室",
            label: "四室",
        }, {
            value: "五室",
            label: "五室",
        }, {
            value: "六室",
            label: "六室",
        }, {
            value: "七室",
            label: "七室",
        }, {
            value: "八室",
            label: "八室",
        }], [{
            value: "一厅",
            label: "一厅",
        }, {
            value: "两厅",
            label: "两厅",
        }, {
            value: "三厅",
            label: "三厅",
        }, {
            value: "四厅",
            label: "四厅",
        }, {
            value: "五厅",
            label: "五厅",
        }, {
            value: "六厅",
            label: "六厅",
        }, {
            value: "七厅",
            label: "七厅",
        }, {
            value: "八厅",
            label: "八厅",
        }]
    ]

    return {
        housingModelStatus, // 房屋模态框状态
        housingFormRef, // 房屋 Form 引用
        showHousingModal, // 模态框是否打开
        housingSubmitLoading, // 提交按钮加载中
        housingRules, // 房屋 Form 校验信息
        housingValue, // 房屋 Form 参数
        houseType, // 户型
    }

}
