// 基本工具

/**
 * 合并对象 obj1 <--add-- obj2
 * @param {对象1} obj 
 * @param {对象2} obj2
 */ 
const mergeObject = (obj, obj2) => {
    for (let key in obj2) {
        if (obj2[key] != null && obj2[key].trim() !== '') {
            obj[key] = obj2[key]
        }
    }
}

const createId = () => {
    let date = Date.now();
    let rund = Math.ceil(Math.random()*1000)
    let id = date + '' + rund;
    return id
}

export {
    mergeObject,
    createId
}
