// 基本工具

/**
 * 合并对象 obj1 <--add-- obj2
 * @param {对象1} obj 
 * @param {对象2} obj2
 */ 
const mergeObject = (obj, obj2) => {
    for (let key in obj2) {
        if (obj2[key] != null) {
            obj[key] = obj2[key]
        }
    }
}

export {
    mergeObject
}
